
public class squaredloop {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 1; i <= 50; i++) {
			System.out.println(i + " squared = " + (i * i));
		}
		
		for (int x = 0; x < 15; x++) {
			System.out.println("hi");
			System.out.println("Hello");
		}
		
		
	}

}
